mapper.update:
UPDATE comment
SET commentDeleted_yn = '0'
WHERE commentNum = #{commentNum}

UPDATE comment
SET commentContents = #{commentContents}, commentDate = NOW()
WHERE commentNum = #{commentNum}

UPDATE board
SET deleted_yn = 'Y'
WHERE boardNum = #{boardNum}

UPDATE board
SET boardTitle = #{boardTitle}, boardContents = #{boardContents}, boardDate = NOW()
WHERE boardNum = #{boardNum}
